mod verifier;

use assert_let_bind::assert_let;
use crux_core::testing::AppTester;
//
use crux_http::protocol::{HttpResponse, HttpResult};
use crux_http::testing::ResponseBuilder;
// use http_types::Body;
use insta::assert_yaml_snapshot as assert_snapshot;
// use insta::internals::Redaction;
use lazy_static::lazy_static;
use serde_json::{json, Value};
use test_utils::wallet;
use vercre_core::vp::{InvokeResponse, RequestObjectResponse, ResponseRequest};
use vercre_wallet::capabilities::signer::{SignerRequest, SignerResponse};
use vercre_wallet::capabilities::store::{StoreEntry, StoreRequest, StoreResponse};
use vercre_wallet::credential::Credential;
use vercre_wallet::presentation::*;

// Test that a 'cross-device' authorization (presentation) request event results in
// the requested VC's being presented.
#[tokio::test]
async fn cross_device_flow() {
    let verifier = verifier::new();

    // instantiate our app via the test harness
    let app = AppTester::<App, _>::default();
    let mut model = Model::default();

    // generate verifier request to 'send' to the app
    let body = json!({
        "purpose": "To verify employment",
        "input_descriptors": [{
            "id": "employment",
            "constraints": {
                "fields": [{
                    "path":["$.type"],
                    "filter": {
                        "type": "string",
                        "const": "EmployeeIDCredential"
                    }
                }]
            }
        }],
        "device_flow": "CrossDevice"
    });

    let resp = verifier.post("/invoke").json(&body).expect_success().await.json::<InvokeResponse>();
    let request_uri = resp.request_uri.expect("request uri");

    // ------------------------------------------------
    // Event::NewRequest
    //   1. creates a StoreRequest to find matching credentials
    //   2. emits Event::Matched
    // ------------------------------------------------
    let mut update = app.update(Event::Requested(request_uri.clone()), &mut model);
    assert_snapshot!("auth-request", app.view(&model));

    // verify the expected StoreRequest was emitted
    assert_let!(Effect::Http(request), &mut update.effects[0]);
    assert_eq!(request.operation.url, request_uri);

    let resp = verifier.get(&request_uri).expect_success().await;

    let req_obj_resp: RequestObjectResponse = resp.json();
    let http_resp = HttpResponse::ok().body(resp.into_bytes()).build();
    let update = app.resolve(request, HttpResult::Ok(http_resp)).expect("an update");

    // check the app emitted an (internal) event to update the model
    let response = ResponseBuilder::ok().body(req_obj_resp.clone()).build();
    assert_eq!(update.events, vec![Event::Fetched(Ok(response.clone()))]);

    // ------------------------------------------------
    // Event::Fetched
    //   1. creates a StoreRequest to find matching credentials
    //   2. emits Event::Matched
    // ------------------------------------------------
    let mut update = app.update(Event::Fetched(Ok(response)), &mut model);
    assert_snapshot!("set-request", app.view(&model));

    // verify the expected StoreRequest was emitted
    assert_let!(Effect::Store(request), &mut update.effects[0]);
    assert_eq!(StoreRequest::List, request.operation);

    // simulate the StoreResponse
    let matched: Credential =
        serde_json::from_value(APP_CREDENTIAL.to_owned()).expect("should deserialize");
    let matches = vec![matched];
    let results = matches
        .iter()
        .map(|m| StoreEntry(serde_json::to_vec(m).expect("should serialize")))
        .collect();
    let response = StoreResponse::List(results);
    let update = app.resolve(request, response).expect("an update");

    // verify the app emitted a Matched event
    let expected = Event::Matched(Ok(matches.clone()));
    assert_eq!(update.events[0], expected);

    // ------------------------------------------------
    // Event::Matched
    //   1. updates the model with the specified credentials
    // ------------------------------------------------
    app.update(Event::Matched(Ok(matches.clone())), &mut model);
    assert_snapshot!("set-credentials", app.view(&model), {
        ".credentials" => insta::sorted_redaction(),
        ".credentials[]" => insta::sorted_redaction(),
        ".credentials[].metadata.credential_definition.credentialSubject" => insta::sorted_redaction()
    });

    // ------------------------------------------------
    // Event::Authorize
    //   1. updates the model status to Authorized
    //   2. creates a SignerRequest get the signer's verification method
    //   3. emits a Proof event
    // ------------------------------------------------
    let mut update = app.update(Event::Authorized, &mut model);
    assert_snapshot!("authorize", app.view(&model), {
        ".credentials" => insta::sorted_redaction(),
        ".credentials[]" => insta::sorted_redaction(),
        ".credentials[].metadata.credential_definition.credentialSubject" => insta::sorted_redaction()
    });
    assert_let!(Effect::Signer(request), &mut update.effects[0]);
    assert_let!(SignerRequest::Verification, &request.operation);

    // simulate the SignerResponse
    let response = SignerResponse::Verification {
        alg: wallet::alg(),
        kid: wallet::kid(),
    };
    let update = app.resolve(request, response).expect("an update");

    // verify the app emitted a Proof event
    assert_let!(Event::Proof(Ok((alg, kid))), &update.events[0]);

    // ------------------------------------------------
    // Event::Proof
    //   1. updates the model with a submission
    //   2. creates a SignerRequest to sign the vp_token
    //   3. emits a Signed event on resolution of request
    // ------------------------------------------------
    let mut update = app.update(Event::Proof(Ok((alg.clone(), kid.clone()))), &mut model);
    assert_snapshot!("set-verification", app.view(&model), {
        ".credentials" => insta::sorted_redaction(),
        ".credentials[]" => insta::sorted_redaction(),
        ".credentials[].metadata.credential_definition.credentialSubject" => insta::sorted_redaction()
    });
    assert_let!(Effect::Signer(request), &mut update.effects[0]);
    assert_let!(SignerRequest::Sign(msg), &request.operation);

    // simulate the SignerResponse
    let signature = wallet::sign(msg);
    let response = SignerResponse::Signature(signature);
    let update = app.resolve(request, response).expect("an update");

    // verify the app emitted a Signed event
    assert_let!(Event::Signed(Ok(signed)), &update.events[0]);

    // ------------------------------------------------
    // Event::Signed
    //   1. creates a SignerRequest to sign the direct post jwt
    //   2. emits a JwtSigned event on resolution of request
    // ------------------------------------------------
    let mut update = app.update(Event::Signed(Ok(signed.to_string())), &mut model);
    assert_let!(Effect::Http(request), &mut update.effects[0]);
    // assert_eq!(request.operation.url, model.request.unwrap().response_uri.unwrap());

    // simulate the HttpResponse from our mock vercre-vp server
    let body = request.operation.body.clone();
    let form = serde_urlencoded::from_bytes::<ResponseRequest>(&body).expect("should deserialize");
    let res = verifier.post("/post").form(&form).expect_success().await;

    let resp: Value = res.json();
    let http_resp = HttpResponse::ok().body(res.into_bytes()).build();
    let update = app.resolve(request, HttpResult::Ok(http_resp)).expect("an update");

    // verify the expected Submitted event was emitted
    let response = ResponseBuilder::ok().body(resp.clone()).build();
    assert_eq!(update.events[0], Event::Submitted(Ok(response.clone())));

    // ------------------------------------------------
    // Event::VerifierResponse
    //   1. updates the model
    // ------------------------------------------------
    app.update(Event::Submitted(Ok(response)), &mut model);
    assert_snapshot!("verifier-response", app.view(&model));
}

